import React from 'react';
import Yorum from './Yorum';
import './Yorumlar.css';

const Yorumlar = props => {
  // üî• Bu bile≈üenin parentƒ±nƒ±n a≈üaƒüƒ±daki proplarƒ± d√ºzg√ºn g√∂nderdiƒüinden emin olun.
  const { yorumlar } = props;

  return (
    <div>
      {yorumlar.map((item) =>
        <Yorum yorum={item} />
      )}
      {/* her g√∂nderi yorumlarƒ± i√ßin map'le i≈üleyerek bir Yorum bile≈üeni d√∂nd√ºr√ºn (proplarƒ±na dikkat ederek) */}
    </div>
  );
};

export default Yorumlar;
